import React, { useEffect, useState } from 'react'
import axios from 'axios'

interface PostProps {
  caption: string,
  content: string,
  userId: string

}
const Post = ({caption, content, userId}: PostProps) => {

  const [user, setUser] = useState("");

  useEffect(() => {
    try {
      const fetchData = async () => {
        const response: any = await axios.get('/api/user/getUser/', { params: { userID: userId } });
        setUser(response.data.name);
      };
      fetchData();
    } catch (error) {
      console.log(error);
    }

  }, [])

  return (
    <div>
      
      <img src={content} />

      <h1>
      {user}: {caption}
      </h1>
      
      </div>
  )
}

export default Post
