import React, { useEffect, useState } from 'react'
import axios from 'axios'



const CreatePost = () => {

    const [title, setTitle] = useState('')
    const [caption, setCaption] = useState('')
    const [image, setImage] = useState('')

    const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
        e.preventDefault();
        
        const post = {
          title: title,
          caption: caption, 
          image: image
        };
    
        try { 
          const response = await axios.post('/api/post/createPost', post);
          console.log(response);
        }
        catch (error) {
          console.log(error);
        }
    }

    const handleImageChange = async (e: React.ChangeEvent<HTMLInputElement>) => {  
        e.preventDefault();
        
        const image = e.target.files?[0] : null;
        const reader = new FileReader();

        reader.onloadend = () => {
            setImage(reader.result as string);
        }
        

    }
    return (
    <div className = 'createPostContainer'>
        <input type="text" placeholder="Title" onChange={(e) => setCaption(e.target.value)}/>   
        <input type="text" placeholder="Caption" onChange={(e) => setCaption(e.target.value)}/>
        <input type="file" accept="image/jpeg" onChange={(e) => handleImageChange(e)} placeholder="Image" />   
        <button type="submit" onClick={(e) => handleSubmit(e)}>Submit</button>
        

    </div>
    
    ) 
 
}

export default CreatePost
