import React, { useEffect, useState } from 'react'
import { useNavigate, Link } from 'react-router-dom'
import axios from 'axios'
import Post from '../components/Post'



const UserProfile = () => {
    const [posts, setPosts] = useState<any>([])
    const [userDetails, setUserDetails] = useState<any>({})
    //uid is user id of current profile, userID is ID of logged in user
    const [uid, setUid] = useState<String>('')
    const { userID } = useParams();

    const navigate = useNavigate();

    //detect scroll to load more posts

    useEffect(() => {
        const handleScroll = () => {
            // Check if the user has scrolled to the bottom

            const isAtBottom = window.innerHeight + window.scrollY >= document.body.offsetHeight;

            if (isAtBottom) {
                console.log('at bottom!');
                //fetch next n posts

            }
        };

        const fetchData = async () => {
            const token = localStorage.getItem('token');
            if (token) {
                try {
                    const response: any = await axios.post('/api/user/getUserByJWTToken', { JWTToken: token });
                    await setUserDetails(response.data);
                    await setUid(response.data._id);
                    const posts: any = await axios.post('/api/user/getUserPosts', { uid: response.data._id});
                    console.log(posts.data.posts)
                    await setPosts(posts.data.posts);
                } catch (error: any) {
                    console.log(error);
                }
            }
            else {
                navigate('/')
            }
        };
        // Attach the scroll event listener when the component mounts
        window.addEventListener('scroll', handleScroll);
        fetchData();
        // Remove the event listener when the component unmounts
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };


    }, []);






    return (
        <div >
            <p>user profile</p>

            <p>{userDetails.name}</p>
            <p>{userDetails.email}</p>

            <div className = "dashContainer">
                {/*posts.map((post: any) => (<Post key={post._id} postId={post._id} caption={post.caption} content={post.content} userId={post.creator} />))*/}

                {posts.map((post: any) => (<Post key={post._id} postId={post._id} caption={post.caption} content={post.content} userId={post.creator}/>)

                )}
            </div>

        </div>

    )

}

export default UserProfile
