import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import axios from 'axios';

interface User { 
    name: string
}

/*
interface Post { 
    caption: string,
    content: string,
    creator: string,
    recipe: string
}
*/

const PostPage = () => {
    //const [post, setPost] = useState<Post | null>(null);
    const [post, setPost] = useState<any>({});
    const [user, setUser] = useState<any>({});
    const { postId } = useParams();

    useEffect(() => {
        const getUserData = async () => { 
            try { 
                console.log(post.creator)
                const response = await axios.get(`/api/user/getUser/${post?.creator}`)
                setUser(response.data)
                console.log(response)
            }
            catch (error) { 
                console.log(error)
            }
        }
        const getPostData = async () => {
            try {
                const response = await axios.get(`/api/post/getPost/${postId}`)
                setPost(response.data)
        
            }
            catch (error) {
                console.log(error)
            }
        }
        
        const getData = async () => {
            try {
                const post = await axios.get(`/api/post/getPost/${postId}`)
                setPost(post.data);
                var userID: any = post.creator;

            }
            catch (error) {
                console.log(error)
            }
        }
        
        getPostData(); 
        getUserData();
        
        }, [postId])
  return (
    <div>

    <img src={post?.content} />
    <p> <strong>{user?.name}</strong> : {post?.caption} </p>
    <p> {post?.recipe} </p>
    
    </div>
  )
}

export default PostPage
